import React, { useState, useEffect, Component } from "react";
import { NavLink, Switch, Route, Redirect } from "react-router-dom";
import ReactDom from "react-dom";
import "./index.css";
import webSocket from "socket.io-client";
import File from "./components/File";
import Chat from "./components/Question";
import Menu from "./components/menu";
import Home from "./Home";
//=============================Variables=================================
function App() {
  // Getting Elements
  const element = id => {
    return document.getElementById(id);
  };
  const status = element("status");
  const messages = element("messages");
  const textarea = element("textarea");
  const username = element("username");
  const clearBtn = element("clear");
  const fileName = element("filename");
  const fileUrl = element("url");
  const [socket, setSocket] = useState(null);
  //==============================Server=================================
  const connectWebSocket = () => {
    setSocket(webSocket("http://localhost:3000"));
    //開啟
  };

  useEffect(() => {
    if (socket) {
      console.log(status.textContent);
      initWebSocket();
    }
  }, [socket]);

  const initWebSocket = () => {
    //對 getMessage 設定監聽，如果 server 有透過 getMessage 傳送訊息，將會在此被捕捉
    socket.on("getMessage", message => {
      console.log(message);
    });

    socket.on("fileOutput", data => {
      const showPdf = element("showPdf");
      console.log(data.url);
      showPdf.setAttribute("src", data.url);
    });

    socket.on("init", data => {
      if (data.length && messages.childNodes.length === 0) updateList(data);
    });

    socket.on("output", data => {
      if (data.length) updateList(data);
    });

    socket.on("cleared", () => {
      clearList();
    });
  };
  //=======================================Ask===========================================
  const sendMessage = () => {
    //以 emit 送訊息，並以 getMessage 為名稱送給 server 捕捉
    socket.emit("getMessage", "只回傳給發送訊息的 client");
  };

  const updateList = data => {
    for (let x = 0; x < data.length; x++) {
      // Building out message div
      const message = document.createElement("div");
      message.setAttribute("class", "chat-message");
      message.textContent = data[x].name + ": " + data[x].body;
      messages.appendChild(message);
      //            messages.insertBefore(message, messages.firstChild)
    }
  };
  const Question = e => {
    if (e.key === "Enter" && e.shiftKey == false) {
      // Emit to server input
      socket.emit("input", {
        name: username.value,
        body: textarea.value
      });
      e.preventDefault();
    }
  };
  //=================================================================================
  const Clear = () => {
    clearList();
    socket.emit("clear");
  };

  // Clear Message

  const clearList = () => {
    messages.textContent = "";
    username.value = "";
    username.focus();
  };
  //======================================File=======================================

  const add = e => {
    if (e.key === "Enter" && e.shiftKey == false) {
      // Emit to server input
      console.log("launch");
      socket.emit("file", {
        filename: fileName.value,
        url: fileUrl.value
      });
    }
  };

  return (
    <div className="Wrapper">
      <input type="button" value="連線" onClick={connectWebSocket} />
      <input type="button" value="送出訊息" onClick={sendMessage} />
      <div className="container">
        <button id="clear" className="btn btn-danger" onClick={Clear}>
          Clear
        </button>
        <div className="row">
          <div className="menu">
            <div>Add</div>
            <div>Class</div>
          </div>
          <div className="Add">
            <File add={add} />
            <div />
            <iframe
              src="https://www.tutorialspoint.com/mongodb/mongodb_tutorial.pdf"
              id="showPdf"
              frameBorder="0"
            />
          </div>
          <Chat ask={Question} />
        </div>
      </div>
    </div>
  );
}

export default App;
