import React, { useState, useEffect } from "react";
import ReactDom from "react-dom";
import "./index.css";
import webSocket from "socket.io-client";
import File from "./components/File";

const Main = () => {
  const [ws, setWs] = useState(null);

  const connectWebSocket = () => {
    setWs(webSocket("http://localhost:3000"));
    //開啟
  };

  useEffect(() => {
    if (ws) {
      //連線成功在 console 中打印訊息
      //console.log("success connect!");
      //設定監聽
      initWebSocket();
    }
  }, [ws]);

  const initWebSocket = () => {
    //對 getMessage 設定監聽，如果 server 有透過 getMessage 傳送訊息，將會在此被捕捉
    ws.on("getMessage", message => {
      console.log(message);
    });
  };

  const sendMessage = () => {
    //以 emit 送訊息，並以 getMessage 為名稱送給 server 捕捉
    ws.emit("getMessage", "只回傳給發送訊息的 client");
  };

  const add = e => {
    var fileUrl = document.getElementById("url");
    var fileName = document.getElementById("filename");

    if (e.key === "Enter" && e.shiftKey == false) {
      // Emit to server input
      console.log("launch");
      ws.emit("file", {
        filename: fileName.value,
        url: fileUrl.value
      });
    }
  };

  return (
    <div className="Wrapper">
      <input type="button" value="連線" onClick={connectWebSocket} />
      <input type="button" value="送出訊息" onClick={sendMessage} />
      <div className="container">
        <button id="clear" className="btn btn-danger">
          Clear
        </button>
        <div className="row">
          <div className="Add">
            <File add={this.add} />
            <iframe
              src="https://www.tutorialspoint.com/mongodb/mongodb_tutorial.pdf"
              id="showPdf"
              frameBorder="0"
            />
          </div>
          <div id="chat">
            <div className="card">
              <div id="messages" className="card-block" />
            </div>
            <br />
            <input
              type="text"
              id="username"
              className="form-control"
              placeholder="Enter name..."
            />
            <br />
            <textarea
              id="textarea"
              className="form-control"
              placeholder="Enter message..."
            />
            <div id="status" />
          </div>
        </div>
      </div>
    </div>
  );
};

ReactDom.render(<Main />, document.getElementById("root"));
