import React, { useState, useEffect } from "react";
import ReactDom from "react-dom";
import "./index.css";
import webSocket from "socket.io-client";
import File from "./components/File";

const Main = () => {
  const [socket, setSocket] = useState(null);

  const connectWebSocket = () => {
    setSocket(webSocket("http://localhost:3000"));
    //開啟
  };

  useEffect(() => {
    if (socket) {
      //連線成功在 console 中打印訊息
      //console.log("success connect!");
      //設定監聽
      initWebSocket();
    }
  }, [socket]);

  const element = id => {
    return document.getElementById(id);
  };

  // Getting Elements
  const status = element("status");
  const messages = element("messages");
  const textarea = element("textarea");
  const username = element("username");
  const clearBtn = element("clear");
  const fileName = element("filename");
  const fileUrl = element("url");

  const initWebSocket = () => {
    //對 getMessage 設定監聽，如果 server 有透過 getMessage 傳送訊息，將會在此被捕捉
    socket.on("getMessage", message => {
      console.log(message);
    });
  };

  const sendMessage = () => {
    //以 emit 送訊息，並以 getMessage 為名稱送給 server 捕捉
    socket.emit("getMessage", "只回傳給發送訊息的 client");
  };

  const add = e => {
    var fileUrl = document.getElementById("url");
    var fileName = document.getElementById("filename");

    if (e.key === "Enter" && e.shiftKey == false) {
      // Emit to server input
      console.log("launch");
      socket.emit("file", {
        filename: fileName.value,
        url: fileUrl.value
      });
    }
  };

  const updateList = data => {
    for (let x = 0; x < data.length; x++) {
      // Building out message div
      const message = document.createElement("div");
      message.setAttribute("class", "chat-message");
      message.textContent = data[x].name + ": " + data[x].body;
      messages.appendChild(message);
      //            messages.insertBefore(message, messages.firstChild)
    }
  };

  const clearList = () => {
    messages.textContent = "";
    username.value = "";
    username.focus();
  };

  // Handling initialization
  socket.on("init", data => {
    if (data.length && messages.childNodes.length === 0) updateList(data);
  });

  fileUrl.addEventListener("keydown", event => {
    if (event.which === 13 && event.shiftKey == false) {
      // Emit to server input
      socket.emit("file", {
        filename: fileName.value,
        url: fileUrl.value
      });

      event.preventDefault();
    }
  });

  // Handling Output
  socket.on("output", data => {
    if (data.length) updateList(data);
  });

  // Getting Status From Server
  socket.on("status", data => {
    setStatus(typeof data === "object" ? data.message : data);

    // If status is clear, clear text
    if (data.clear) {
      textarea.value = "";
    }
  });

  socket.on("fileOutput", data => {
    const showPdf = element("showPdf");

    showPdf.setAttribute("src", data.url);
  });

  // Handle Input
  textarea.addEventListener("keydown", event => {
    if (event.which === 13 && event.shiftKey == false) {
      // Emit to server input
      socket.emit("input", {
        name: username.value,
        body: textarea.value
      });

      event.preventDefault();
    }
  });

  // Handle Chat Clear
  clearBtn.addEventListener("click", () => {
    clearList();
    socket.emit("clear");
  });

  // Clear Message
  socket.on("cleared", () => {
    clearList();
  });

  return (
    <div className="Wrapper">
      <input type="button" value="連線" onClick={connectWebSocket} />
      <input type="button" value="送出訊息" onClick={sendMessage} />
      <div className="container">
        <button id="clear" className="btn btn-danger">
          Clear
        </button>
        <div className="row">
          <div className="Add">
            <File add={add} />
            <iframe
              src="https://www.tutorialspoint.com/mongodb/mongodb_tutorial.pdf"
              id="showPdf"
              frameBorder="0"
            />
          </div>
          <div id="chat">
            <div className="card">
              <div id="messages" className="card-block" />
            </div>
            <br />
            <input
              type="text"
              id="username"
              className="form-control"
              placeholder="Enter name..."
            />
            <br />
            <textarea
              id="textarea"
              className="form-control"
              placeholder="Enter message..."
            />
            <div id="status" />
          </div>
        </div>
      </div>
    </div>
  );
};

ReactDom.render(<Main />, document.getElementById("root"));
