import React, { Component } from "react";

export default class PostRender extends Component {
  render() {
    let list = this.props.fileArray.slice(0);
    let show = list.find(function(item, index, array) {
      return item._id == this.props.match.params;
    });
    return (
      <div>
        <iframe src={show._id} id="showPdf" frameBorder="0" />
      </div>
    );

    /*
    return id && postIDs.includes(id) ? (
      <></>
    ) : (
      <div>
        <h3>No more to show</h3>
      </div>
    );
    */
  }
}
